INSERT INTO courses_public (name, slug, short_description, long_description, cta_text, hero_image_url, flag_image_url, features, duration, level, price, color_scheme, order_index, is_active) VALUES
('Inglês', 'ingles-basico', 'Expanda seu domínio do inglês com cursos abrangentes; domine a língua global e abra portas para um futuro brilhante.', 'Aprenda inglês com nossa metodologia inovadora que combina aulas personalizadas, foco em fluência e conversação, preparação para testes de proficiência (TOEFL, IELTS, Cambridge English) e material didático exclusivo incluindo o Livro de Inglês da BigStep e ''English for Everyone''.', 'Começar Agora', '/images/courses/english-hero.jpg', '/images/flags/usa-flag.png', '["Aulas Personalizadas (particulares ou em grupo)", "Fluência e Preparação para Entrevistas", "Preparação para TOEFL, IELTS e Cambridge", "Material Didático Exclusivo BigStep"]', '6-12 meses', 'Iniciante ao Avançado', 'A partir de R$ 299/mês', 'from-blue-500 to-blue-600', 1, true),
('Alemão', 'alemao', 'Descubra o idioma alemão com cursos especializados; amplie suas oportunidades pessoais e profissionais.', 'Aprenda alemão com cursos especializados que incluem aulas personalizadas individuais ou em grupo, cursos intensivos para imigração, preparação para TestDaF, DSH, ÖSD e Goethe-Zertifikat, e material didático exclusivo incluindo ''Aspekte'', ''Momente'' e ''BigStep Deutsch''.', 'Começar Agora', '/images/courses/german-hero.jpg', '/images/flags/german-flag.png', '["Aulas Personalizadas (individuais ou em grupo)", "Cursos Intensivos para Imigração", "Preparação para TestDaF, DSH, ÖSD e Goethe", "Material Didático Exclusivo BigStep"]', '8-14 meses', 'Iniciante ao Avançado', 'A partir de R$ 349/mês', 'from-gray-800 to-gray-900', 2, true),
('Espanhol', 'espanhol', 'Aprenda espanhol de forma dinâmica e envolvente; desenvolva habilidades comunicativas e mergulhe na cultura hispânica.', 'Aprenda espanhol de forma dinâmica com aulas personalizadas individuais ou em grupo, foco em fluência e preparação para entrevistas, preparação para DELE e CELU (útil para UBA e pós-graduação na Espanha), e material didático de alta qualidade incluindo ''Español en Marcha'', ''Nuevo Ven'' e ''Aula Internacional''.', 'Começar Agora', '/images/courses/spanish-hero.jpg', '/images/flags/spain-flag.png', '["Aulas Personalizadas (individuais ou em grupo)", "Fluência e Preparação para Entrevistas", "Preparação para DELE e CELU", "Material Didático de Alta Qualidade"]', '8-14 meses', 'Iniciante ao Avançado', 'A partir de R$ 349/mês', 'from-green-500 to-green-600', 3, true),
('Italiano', 'italiano', 'Descubra a língua italiana com cursos especializados; amplie suas oportunidades pessoais e profissionais.', 'Aprenda italiano com cursos especializados que incluem aulas personalizadas individuais ou em grupo, cursos intensivos para emprego e desenvolvimento de carreira, preparação para CILS e CELI, e material didático exclusivo incluindo ''Al Dente'' e ''BigStep Italiano''.', 'Começar Agora', '/images/courses/italian-hero.jpg', '/images/flags/italy-flag.png', '["Aulas Personalizadas (individuais ou em grupo)", "Cursos Intensivos para Carreira", "Preparação para CILS e CELI", "Material Didático Exclusivo BigStep"]', '8-14 meses', 'Iniciante ao Avançado', 'A partir de R$ 349/mês', 'from-green-600 to-green-700', 4, false),
('Francês', 'frances', 'Descubra a língua francesa com cursos especializados; amplie suas oportunidades pessoais e profissionais.', 'Aprenda francês com cursos especializados que incluem aulas personalizadas individuais ou em grupo, cursos intensivos para emprego e crescimento pessoal, preparação para DELF e DALF, e material didático exclusivo incluindo ''Odyssee'' e ''BigStep Francês''.', 'Começar Agora', '/images/courses/french-hero.jpg', '/images/flags/france-flag.png', '["Aulas Personalizadas (individuais ou em grupo)", "Cursos Intensivos para Carreira", "Preparação para DELF e DALF", "Material Didático Exclusivo BigStep"]', '8-14 meses', 'Iniciante ao Avançado', 'A partir de R$ 349/mês', 'from-purple-500 to-purple-600', 5, false);
